package org.example;

import org.example.csvLoader.CSVLoader;
import org.example.h2Loader.H2Loader;
import org.example.mockProvider.MockProvider;
import org.example.portfolio.Portfolio;
import org.example.simpleOptionFormula.SimpleOptionFormula;

public class App {
    private static final int sleepTime = 1000;

    public static void main(String[] args) {
        Portfolio p = new Portfolio();
        CSVLoader c = new CSVLoader();
        H2Loader h = new H2Loader();
        MockProvider m = new MockProvider();
        SimpleOptionFormula f = new SimpleOptionFormula();
        p.setPorfolioLoader(c);
        p.setPorfolioLoader(h);
        p.setDataProvider(m);
        p.setOptionFormula(f);
        p.load();

        try {
            while(true){
                    System.out.println("\033[H\033[2J");
                    System.out.println("====================================================================================================================");
                    System.out.println("|Ticker                   |Shares   |Type  |Strike      |Maturity          |Asset    |Price       |Value           |");
                    System.out.println("====================================================================================================================");
                    p.getProducts().values().forEach(System.out::println);
                    System.out.println("====================================================================================================================");
                    System.out.println();
                    System.out.println();
                    System.out.println("=====================");
                    System.out.println("NAV: " + p.getNAV());
                    System.out.println("=====================");
                    System.out.println("Ctrl + C to exit");
                    Thread.sleep(sleepTime);
            }
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
    }
}
